mapper.select:
SELECT id, title, content, create_time as createTime, update_time as updateTime, user_id as userId, tag, is_top as isTop, is_delete as isDelete, status, channel_id as channelId, img, push, is_comment as isComment, hits, loves, collections, static_link as staticLink, keywords, description, sub_title as subTitle, type, category_id as categoryId, thumnail from content
and channel_id=#{channelId}
and title LIKE '%${keyword}%'
and is_delete LIKE '%${isDelete}%'
ORDER BY ${order} LIMIT #{pageNo},#{pageSize};

mapper.update:
update content
status=#{status}, is_delete=#{isDelete}, is_comment=#{isComment}, is_top=#{isTop}, push=#{push},
where id=#{id}
